<template>
  <div class="setting-wrapper">
    <div class="child-mod">
      <div class="span">字号</div>
      <text class="spe-button" @click="bindBig">大</text>
      <text class="spe-button" style="margin-left:20px;" @click="bindSmall">
       小
      </text>
    </div>
    <div class="child-mod">
      <div class="span">时间</div>
      <text class="spe-button" @click="bindDay">日间</text>
      <text class="spe-button" style="margin-left:20px;" @click="bindNight">夜晚
      </text>
    </div>
  </div>
</template>
<script>
// vue-tiny-rate
export default {
  props: ['initFontSize', 'dayOrnight'],
  computed: {
  },
  methods: {
    bindBig() {
      if (this.initFontSize > 20){
        return;
      }
      let FontSize=parseInt(this.initFontSize)
      FontSize = FontSize+ 1;
      this.$emit('changeFontSize', FontSize)
    },

    bindSmall() {
      if (this.initFontSize < 12) {
        return;
      }
      let FontSize=parseInt(this.initFontSize)
      FontSize = FontSize - 1;
      this.$emit('changeFontSize', FontSize)
    },

    bindDay() {
      this.$emit('changeBgc','day')
    },

    bindNight() {
      this.$emit('changeBgc', 'night')
    }

  }
}
</script>
<style>
.child-mod {
  padding: 1px 10px;
  margin-top: 10px
}
.child-mod .span {
  display: inline-block;
  padding-right: 25px;
  padding-left: 10px;
}
.spe-button{
  background: none;
  border: 1px #8c8c8c solid;
  width: 102px;
  height: 30px;
  line-height: 30px;
  text-align: center;
  color: #555;
  display: inline-block;
  border-radius: 16px;
}
.day {
  border-color: #2dabf6;
}
.night {
  background-color: black;
}

